CREATE TABLE `iweixincard_card` (
  `_id` char(24) NOT NULL DEFAULT '' COMMENT '卡券ID',
  `card_id` char(32) NOT NULL DEFAULT '' COMMENT '微信公众平台的卡券ID',
  `card_type` varchar(24) NOT NULL DEFAULT '' COMMENT '卡券类型 例如GENERAL_COUPON',
  `logo_url` varchar(128) NOT NULL DEFAULT '' COMMENT '卡券的商户logo，尺寸为 300*300 例如http://mmbiz.qpic.cn/',
  `code_type` varchar(16) NOT NULL DEFAULT '' COMMENT 'code码展示类型 例如CODE_TYPE_TEXT',
  `brand_name` varchar(36) NOT NULL DEFAULT '' COMMENT '商户名字,字数上限为12个汉字 例如海底捞',
  `title` varchar(27) NOT NULL DEFAULT '' COMMENT '券名，字数上限为9个汉字 例如双人套餐100元兑换券',
  `sub_title` varchar(54) NOT NULL DEFAULT '' COMMENT '券名的副标题，字数上限为18个汉字 例如鸳鸯锅底+牛肉1份+土豆一份',
  `color` varchar(16) NOT NULL DEFAULT '' COMMENT '券颜色。按色彩规范标注填写Color010-Color100',
  `notice` varchar(48) NOT NULL DEFAULT '' COMMENT '卡券使用提醒，字数上限为16个汉字。例如请出示二维码核销卡券',
  `description` text NOT NULL COMMENT '卡券使用说明，字数上限为1024个汉字。例如不可与其他优惠同享/n如需团购券发票，请向店员提出要求。',
  `sku_quantity` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '卡券库存的数量，上限为100000000 例如10000',
  `date_info_type` varchar(30) NOT NULL DEFAULT '' COMMENT '使用时间的类型，旧文档采用的1和2依然生效',
  `date_info_begin_timestamp` datetime NOT NULL COMMENT 'type为DATE_TYPE_FIX_TIME_RANGE时专用，表示起用时间。从1970年1月1日00:00:00至起用时间的秒数，最终需转换为字符串形态传入。（东八区时间，单位为秒）',
  `date_info_end_timestamp` datetime NOT NULL COMMENT 'type为DATE_TYPE_FIX_TIME_RANGE时，表示卡券统一的结束时间，建议设置为截止日期的23:59:59过期。（东八区时间，单位为秒）',
  `date_info_fixed_term` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT 'type为DATE_TYPE_FIX_TERM时专用，表示自领取后多少天内有效，不支持填写0。例如15',
  `date_info_fixed_begin_term` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '	type为DATE_TYPE_FIX_TERM时专用，表示自领取后多少天开始生效，领取后当天生效填写0。（单位为天）例如0',
  `date_info_fixed_end_timestamp` datetime NOT NULL COMMENT '可用于DATE_TYPE_FIX_TERM时间类型，表示卡券统一过期时间，建议设置为截止日期的23:59:59过期。（东八区时间，单位为秒），设置了fixed_term卡券，当时间达到end_timestamp时卡券统一过期',
  `use_custom_code` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否自定义Code码。填写true或false，默认为false。通常自有优惠码系统的开发者选择自定义Code码，并在卡券投放时带入Code码，详情见是否自定义Code码。',
  `bind_openid` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否指定用户领取，填写true或false。默认为false。通常指定特殊用户群体投放卡券或防止刷券时选择指定用户领取。',
  `service_phone` varchar(24) NOT NULL DEFAULT '' COMMENT '客服电话 例如40012234',
  `location_id_list` text NOT NULL COMMENT '门店位置ID 例如1234,2312',
  `source` varchar(36) NOT NULL DEFAULT '' COMMENT '第三方来源名，例如同程旅游、 格瓦拉',
  `center_title` varchar(18) NOT NULL DEFAULT '' COMMENT '卡券顶部居中的按钮，仅在卡券状态正常(可以核销)时显示，建议开发者设置此按钮时code_type选择CODE_TYPE_NONE类型。例如立即使用',
  `center_sub_title` varchar(24) NOT NULL DEFAULT '' COMMENT '显示在入口下方的提示语，仅在卡券状态正常(可以核销)时显示。例如立即享受优惠',
  `center_url` varchar(128) NOT NULL DEFAULT '' COMMENT '顶部居中的url，仅在卡券状态正常(可以核销)时显示。例如www.xxx.com',
  `custom_url_name` varchar(15) NOT NULL DEFAULT '' COMMENT '自定义跳转外链的入口名字。详情见活用自定义入口 例如立即使用',
  `custom_url` varchar(128) NOT NULL DEFAULT '' COMMENT '自定义跳转的URL。例如xxxx.com',
  `custom_url_sub_title` varchar(18) NOT NULL DEFAULT '' COMMENT '显示在入口右侧的提示语。例如更多惊喜',
  `promotion_url_name` varchar(15) NOT NULL DEFAULT '' COMMENT '营销场景的自定义入口名称。例如产品介绍',
  `promotion_url` varchar(128) NOT NULL DEFAULT '' COMMENT '入口跳转外链的地址链接。例如xxxx.com',
  `promotion_url_sub_title` varchar(18) NOT NULL DEFAULT '' COMMENT '显示在营销入口右侧的提示语。例如更多惊喜',
  `use_limit` smallint(4) unsigned NOT NULL DEFAULT '1' COMMENT '每人使用次数限制',
  `get_limit` smallint(4) unsigned NOT NULL DEFAULT '1' COMMENT '每人可领券的数量限制,不填写默认为50',
  `can_share` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '卡券领取页面是否可分享',
  `can_give_friend` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '卡券是否可转赠。',
  `status` char(30) NOT NULL DEFAULT '' COMMENT '卡券状态',
  `get_custom_code_mode` varchar(30) NOT NULL DEFAULT '' COMMENT '获取自定义code的方式',
  `shake_slogan_title` varchar(50) NOT NULL DEFAULT '' COMMENT '新年祝语标题',
  `shake_slogan_sub_title` varchar(50) NOT NULL DEFAULT '' COMMENT '新年祝语正文',
  `can_shake` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '参加摇礼券活动的标志位',
  `weixin_title` varchar(50) NOT NULL DEFAULT '' COMMENT '微信公众推送标题',
  `weixin_image` varchar(100) NOT NULL DEFAULT '' COMMENT '微信公众推送封面图片',
  `weixin_desc` varchar(1000) NOT NULL DEFAULT '' COMMENT '微信公众推送摘要',
  `weixin_url` varchar(100) NOT NULL DEFAULT '' COMMENT '微信公众推送URL',
  `general_coupon_default_detail` text NOT NULL COMMENT '优惠券专用，优惠详情。例如音乐木盒。',
  `groupon_deal_detail` text NOT NULL COMMENT '团购券专用，团购详情。例如双人套餐\\n -进口红酒一支。\\n孜然牛肉一份',
  `discount_discount` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '折扣券专用，表示打折额度（百分比）。填30就是七折。',
  `gift_gift` text NOT NULL COMMENT '兑换券专用，兑换内容。例如可兑换音乐木盒一个。',
  `cash_least_cost` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '代金券专用，表示起用金额（单位为分）。,如果无起用门槛则填0。',
  `cash_reduce_cost` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '代金券专用，表示减免金额（单位为分）。,如果无起用门槛则填0。',
  `member_card_supply_bonus` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否支持积分，填写 true 或 false，如填写 true，积分相关字段均为必填。填写 false，积分字段无需填写。储值字段处理方式相同。',
  `member_card_supply_balance` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否支持储值，填写 true 或false。 （该权限申请及说明详见Q&A 文档)',
  `member_card_custom_field1_name_type` varchar(50) NOT NULL DEFAULT '' COMMENT '自定义会员信息类目1名称',
  `member_card_custom_field1_url` varchar(100) NOT NULL DEFAULT '' COMMENT '自定义会员信息类目1自定义跳转url',
  `member_card_custom_field2_name_type` varchar(50) NOT NULL DEFAULT '' COMMENT '自定义会员信息类目2名称',
  `member_card_custom_field2_url` varchar(100) NOT NULL DEFAULT '' COMMENT '自定义会员信息类目2自定义跳转url',
  `member_card_custom_field3_name_type` varchar(50) NOT NULL DEFAULT '' COMMENT '自定义会员信息类目3名称',
  `member_card_custom_field3_url` varchar(100) NOT NULL COMMENT '自定义会员信息类目3自定义跳转url',
  `member_card_bonus_cleared` varchar(50) NOT NULL DEFAULT '' COMMENT '积分清零规则。 否',
  `member_card_bonus_rules` varchar(50) NOT NULL DEFAULT '' COMMENT '积分规则。 否',
  `member_card_balance_rules` varchar(50) NOT NULL DEFAULT '' COMMENT '储值说明。 否',
  `member_card_prerogative` varchar(50) NOT NULL DEFAULT '' COMMENT '特权说明。 是',
  `member_card_bind_old_card_url` varchar(100) NOT NULL DEFAULT '' COMMENT '绑定旧卡的 url，与“ activate_url”字段二选一必填，用户领取会员卡后显示“ 绑定会员卡” 。',
  `member_card_activate_url` varchar(100) NOT NULL DEFAULT '' COMMENT '激活会员卡的 url，与“ bind_old_card_url”字段二选一必填。 用户领取会员卡后显示“激活会员卡”。',
  `member_card_need_push_on_view` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT 'true 为用户点击进入会员卡时是否推送事件。',
  `member_card_custom_cell1_name` varchar(50) NOT NULL DEFAULT '' COMMENT '会员卡类型专属营销入口1入口名称',
  `member_card_custom_cell1_tips` varchar(20) NOT NULL DEFAULT '' COMMENT '会员卡类型专属营销入口1入口右侧提示语，6个汉字内',
  `member_card_custom_cell1_url` varchar(100) NOT NULL DEFAULT '' COMMENT '会员卡类型专属营销入口1入口跳转链接',
  `member_card_custom_cell2_name` varchar(50) NOT NULL DEFAULT '' COMMENT '会员卡类型专属营销入口2入口名称',
  `member_card_custom_cell2_tips` varchar(20) NOT NULL DEFAULT '' COMMENT '会员卡类型专属营销入口2入口右侧提示语，6个汉字内',
  `member_card_custom_cell2_url` varchar(100) NOT NULL DEFAULT '' COMMENT '会员卡类型专属营销入口2入口跳转链接',
  `scenic_ticket_ticket_class` text NOT NULL COMMENT '景区门票专用，票类型 例如平日全票，套票等。',
  `scenic_ticket_guide_url` varchar(128) NOT NULL DEFAULT '' COMMENT '景区门票专用，导览图url 例如xxx.com',
  `movie_ticket_detail` text NOT NULL COMMENT '电影票类型专用，电影票详情 例如电影名：xxx，电影简介：xxx。',
  `boarding_pass_from` varchar(54) NOT NULL DEFAULT '' COMMENT '飞机票类型专用，起点，上限为18个汉字。例如成都',
  `boarding_pass_to` varchar(54) NOT NULL DEFAULT '' COMMENT '飞机票类型专用，终点，上限为18个汉字。例如广州',
  `boarding_pass_flight` varchar(24) NOT NULL DEFAULT '' COMMENT '飞机票类型专用，航班 例如CE123',
  `boarding_pass_gate` varchar(12) NOT NULL DEFAULT '' COMMENT '飞机票类型专用，入口，上限为4个汉字。例如A11',
  `boarding_pass_check_in_url` varchar(128) NOT NULL DEFAULT '' COMMENT '飞机票类型专用，在线值机的链接 例如xxx.com',
  `boarding_pass_air_model` varchar(24) NOT NULL DEFAULT '' COMMENT '飞机票类型专用，机型，上限为8个汉字。例如空客A320',
  `boarding_pass_departure_time` datetime NOT NULL COMMENT '飞机票类型专用，起飞时间。Unix时间戳格式。',
  `boarding_pass_landing_time` datetime NOT NULL COMMENT '飞机票类型专用，降落时间。Unix时间戳格式。',
  `meeting_ticket_meeting_detail` text NOT NULL COMMENT '会议门票类型专用，会议详情 例如本次会议于2015年5月10号在广州举行，会场地点：xxxx。',
  `meeting_ticket_map_url` varchar(128) NOT NULL DEFAULT '' COMMENT '会议门票类型专用，会场导览图 例如xxx.com',
  `received_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '领取数',
  `consumed_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '核销数',
  `deleted_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '删除数',
  `unavailable_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '失效数',
  `give_by_friend_num` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '转赠朋友数',
  `memo` text NOT NULL COMMENT '备注',
  `__CREATE_TIME__` datetime NOT NULL COMMENT '创建时间',
  `__MODIFY_TIME__` datetime NOT NULL COMMENT '修改时间',
  `__REMOVED__` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否删除',
  PRIMARY KEY (`_id`),
  KEY `NewIndex1` (`card_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC COMMENT='微信卡券-卡券';

