CREATE TABLE `iweixincard_card_bag` (
  `_id` char(24) NOT NULL DEFAULT '' COMMENT '卡包ID',
  `card_id` char(32) NOT NULL DEFAULT '' COMMENT '微信卡券ID',
  `UserCardCode` char(20) NOT NULL DEFAULT '' COMMENT 'code序列号',
  `FromUserName` char(32) NOT NULL DEFAULT '' COMMENT '领券方帐号',
  `is_got` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '用户是否领券',
  `got_time` datetime NOT NULL COMMENT '领券时间',
  `IsGiveByFriend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否为转赠，1 代表是，0 代表否。',
  `FriendUserName` char(32) NOT NULL DEFAULT '' COMMENT '赠送方账号',
  `OldUserCardCode` char(20) NOT NULL COMMENT '转赠前的code序列号',
  `IsRestoreMemberCard` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否是会员卡',
  `IsRecommendByFriend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否为朋友推荐',
  `SourceScene` char(30) NOT NULL DEFAULT '' COMMENT '来源场景',
  `encrypt_code` char(128) NOT NULL DEFAULT '' COMMENT '编码code',
  `new_code` char(20) NOT NULL DEFAULT '' COMMENT '新的卡券code编码',
  `is_consumed` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已核销',
  `StaffOpenId` char(32) NOT NULL DEFAULT '' COMMENT '核销该卡券核销员的openid（只有通过卡券商户助手核销时才会出现）',
  `consume_time` datetime NOT NULL COMMENT '核销时间',
  `ConsumeSource` char(30) NOT NULL DEFAULT '' COMMENT '核销来源。支持开发者统计API核销（FROM_API）、公众平台核销（FROM_MP）、卡券商户助手核销（FROM_MOBILE_HELPER）（核销员微信号）',
  `LocationId` char(30) NOT NULL DEFAULT '' COMMENT '门店ID，当前卡券核销的门店名称（只有通过卡券商户助手和买单核销时才会出现）',
  `LocationName` varchar(30) NOT NULL DEFAULT '' COMMENT '门店名称，当前卡券核销的门店名称（只有通过卡券商户助手和买单核销时才会出现）',
  `is_deleted` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否删除',
  `delete_time` datetime NOT NULL COMMENT '删除时间',
  `is_unavailable` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已失效',
  `unavailable_time` datetime NOT NULL COMMENT '失效时间',
  `is_give_to_friend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已赠予朋友',
  `give_to_friend_time` datetime NOT NULL COMMENT '赠予朋友时间',
  `friend_card_bag_id` char(24) NOT NULL COMMENT '朋友卡包ID',
  `OuterId` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '场景参数值',
  `memo` text NOT NULL COMMENT '备注',
  `__CREATE_TIME__` datetime NOT NULL COMMENT '创建时间',
  `__MODIFY_TIME__` datetime NOT NULL COMMENT '修改时间',
  `__REMOVED__` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否删除',
  PRIMARY KEY (`_id`),
  KEY `NewIndex4` (`card_id`,`UserCardCode`,`FromUserName`)
