/*Table structure for table `iweixincard_logo` */

DROP TABLE IF EXISTS `iweixincard_logo`;

CREATE TABLE `iweixincard_logo` (
  `_id` char(24) NOT NULL DEFAULT '' COMMENT '卡券的商户LOGO ID',
  `logo` varchar(128) NOT NULL DEFAULT '' COMMENT '卡券的商户logo图片文件',
  `logo_url` varchar(128) NOT NULL DEFAULT '' COMMENT '卡券的商户logo的url',
  `is_uploaded` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已上传',
  `__CREATE_TIME__` datetime NOT NULL COMMENT '创建时间',
  `__MODIFY_TIME__` datetime NOT NULL COMMENT '修改时间',
  `__REMOVED__` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否删除',
  PRIMARY KEY (`_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC COMMENT='微信卡券-商户logo';

