/*Table structure for table `iweixincard_poi` */

DROP TABLE IF EXISTS `iweixincard_poi`;

CREATE TABLE `iweixincard_poi` (
  `_id` char(24) NOT NULL DEFAULT '' COMMENT '门店ID',
  `poi_id` char(10) NOT NULL DEFAULT '' COMMENT '微信公众平台上的门店ID。',
  `sid` varchar(10) NOT NULL DEFAULT '' COMMENT '商户自己的 id',
  `business_name` varchar(20) NOT NULL DEFAULT '' COMMENT '门店名称',
  `branch_name` varchar(20) NOT NULL DEFAULT '' COMMENT '分店名',
  `province` varchar(10) NOT NULL DEFAULT '' COMMENT '门店所在的省',
  `city` varchar(10) NOT NULL DEFAULT '' COMMENT '门店所在的市',
  `district` varchar(20) NOT NULL DEFAULT '' COMMENT '门店所在的区',
  `address` varchar(50) NOT NULL DEFAULT '' COMMENT '门店所在的详细街道地址',
  `telephone` char(20) NOT NULL DEFAULT '' COMMENT '门店的电话',
  `categories` text NOT NULL COMMENT '门店的类型',
  `offset_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '坐标类型',
  `longitude` float unsigned NOT NULL DEFAULT '0' COMMENT '经度',
  `latitude` float unsigned NOT NULL DEFAULT '0' COMMENT '纬度',
  `photo_list` text NOT NULL COMMENT '图片列表',
  `recommend` varchar(100) NOT NULL DEFAULT '' COMMENT '推荐品',
  `special` varchar(100) NOT NULL DEFAULT '' COMMENT '特色服务',
  `introduction` varchar(100) NOT NULL DEFAULT '' COMMENT '商户简介',
  `open_time` varchar(10) NOT NULL DEFAULT '' COMMENT '营业时间',
  `avg_price` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '人均价格',
  `available_state` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '门店的可用状态',
  `update_status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '更新状态',
  `mobile` char(11) NOT NULL DEFAULT '' COMMENT '移动电话',
  `is_uploaded` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否已上传',
  `memo` text NOT NULL COMMENT '备注',
  `__CREATE_TIME__` datetime NOT NULL COMMENT '创建时间',
  `__MODIFY_TIME__` datetime NOT NULL COMMENT '修改时间',
  `__REMOVED__` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否删除',
  PRIMARY KEY (`_id`),
  KEY `NewIndex1` (`poi_id`),
  KEY `NewIndex2` (`sid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC COMMENT='微信卡券-POI门店';

